import React from "react";
import { useLocation } from "react-router-dom";

// 各選択項目に対応する画像のURLを用意します
const images = {
  "グルメ": "/images/gourmet.jpg",
  "歴史、文化": "/images/history.jpg",
  "自然": "/images/nature.jpg",
  "ショッピング": "/images/shopping.jpg",
  "スポーツ": "/images/sports.jpg",
  "居酒屋": "/images/izakaya.jpg",
  "温泉": "/images/onsen.jpg",
};

const ResultPage = () => {
  const location = useLocation();
  const { selectedPreferences } = location.state || { selectedPreferences: [] };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>選択されたジャンル</h2>
      {selectedPreferences.length > 0 ? (
        <div>
          {selectedPreferences.map((genre, index) => (
            <div key={index} style={{ marginBottom: "20px" }}>
              <h3>{genre}</h3>
              {images[genre] && (
                <img
                  src={images[genre]}
                  alt={genre}
                  style={{ width: "100%", height: "auto" }}
                />
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>選択がありません。</p>
      )}
    </div>
  );
};

export default ResultPage;
